INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (1, 1, 'Bevacizumab/Irinotecan', '<b>Bevacizumab/Irinotecan</b>', '<b>Bevacizumab</b> 15 mg/kg IV<br> Repeat Every 21 Days<br> <b>Irinotecan</b> 350 mg/m<sup>2</sup> with enzyme-inducing antiepileptic drugs, days 1, 8, 22, 29<br> <b>OR</b><br> <b>Irinotecan</b> 125 mg/m<sup>2</sup> with non-enzyme-including antiepileptic drugs, IV, days 1, 8, 22, 29<br> Repeat every 6 weeks', 'Moderate', 'Vredenburgh JJ, Desjardins A, Herndon JE, et al. Bevacizumab plus irinotecan in recurrent glioblastoma multiforme. $J Clin Oncol. 2007;25(30):4722-4279.$', '<b>Hepatic Impairment</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Irinotecan:<br>No dose reduction </li></ul>', 'Bevacizumab : Avastin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (2, 1, 'PCV', '<b>PCV</b>', '<b>Procarbazine</b> 60 mg/m<sup>2</sup>/d PO, days 8-21<br> <b>Lomustine</b> 110 mg/m<sup>2</sup> PO, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, days 8, 29<br> Repeat every 6 weeks upto 7 cycles ', 'Moderate-High', 'Levin VA, Uhm JH, Jaeckle KA, et al. Phase III randomized study of postradiotherapy chemotherapy with alpha-difluromethylornithine- procarbazine, N-(2-chloroethyl)-N'' -cyclohexyl- N-nitrosuera, vincristine (DFMO-PCV) versus PCV for glioblastoma multiforme. $Clin Cancer Res.2000;6(10):3878-3884.$', '<b>Hepatic Impairment</b><ul><li>Lomustine:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Lomustine:<br>If CrCl < 60 mL/min, do not use</li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Lomustine : CCNU<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (3, 1, 'Temozolomide+RT', '<b>Temozolomide+RT</b>', '<b>Temozolomide</b> 75 mg/m<sup>2</sup> daily along with radiation<br> <b>FOLLOWED 28 DAYS LATER BY</b><br><b>Temozolomide<sup>e</sup></b> 150 mg/m<sup>2</sup> daily for 5 days<br>Repeat every 28 days.', 'Moderate for IV<br> <=75 mg/m<sup>2</sup> PO is minimal-low;<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', 'Stupp R, et al.  Radiotherapy plus Concomitant and Adjuvant Temozolomide for Glioblastoma.$N Engl J Med 2005; 352:987-996$', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Temozolomide : Temodar');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (4, 1, 'BCNU (Carmustine)', '<b>BCNU (Carmustine)</b>', '<b>Carmustine</b> 200 mg/m<sup>2</sup> IV over 30-60 minutes<br> Repeat every 8 weeks ', 'High', 'Selker RG, Sharipo WR, Burger p, et al. The Brain tumor cooperative group NIH Trial 87-01: a randomized comparision of surgery, external radiotherapy, and carmustine versus surgery, interstital radiotherapy, and carmustine. $Neurosurgery.2002;51(2):343-355; discussion 355-357.$', '<b>Hepatic Impairment</b><ul><li>Carmustine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carmustine:<br>If CrCl < 60 mL/min, do not use</li></ul>', 'Carmustine : BCNU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (5, 1, 'Bevacizumab', '<b>Bevacizumab</b>', '<b>Bevacizumab</b> 10 mg/kg IV<br> Repeat every 14 days on a 28-day cycle<br><b>OR</b><br><b>Bevacizumab</b> 15 mg/kg IV<br> Repeat every 21 days ', 'Low', 'Kreisl TN, Kim L, Moore K, et al. Phase II trial of single-agent bevacizumab plus irinotecan at tumor progression in recuurent glioblastoma. $J Clin Oncol. 2009;27(5):740-745.$                                                           Raizer JJ, Grimm S, et al. Aphase 2 trial of single-agent bevacizumab plus irinotecan at tumor progression in recurrent high- grade schedule for patients with recurrent high- grade gliomas. $Cancer.2010;116(22):597-5305.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Bevacizumab : Avastin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (6, 1, 'Temozolomide', '<b>Temozolomide</b>', '<b>Temozolomide</b> 150 mg/m<sup>2</sup> every day for 5 days per 28-day cycle<br> ', 'Moderate for IV<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', 'Gállego Pérez-Larraya J, et al.Temozolomide in elderly patients with newly diagnosed glioblastoma and poor performance status: an ANOCEF phase II trial.$J Clin Oncol. 2011 Aug 1;29(22):3050-5. doi: 10.1200/JCO.2011.34.8086. Epub 2011 Jun 27$', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Temozolomide : Temodar');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (7, 2, 'Carboplatin/Fluorouracil/Cetuximab', '<b>Carboplatin/Fluorouracil/Cetuximab</b>', '<b>Carboplatin</b> AUC of 5, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1 <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week.', 'Moderate', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Carboplatin : Paraplatin<br>Cetuximab : Erbitux<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (8, 2, 'CF', '<b>CF</b>', '<b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>5-Fluorouracil</b> 1000 mg/m<sup>2</sup>/day CIVI, days 1-4<br> Repeat every 21 days', 'High', 'Gibson MK, Li Y, Murphy B, et al. Randomized phase III evaluation of cisplatin plus fluorouracil versus cisplatin plus paclitaxel in advanced head and neck cancer (E1395): an intergroup trial of the Eastern Cooperative Oncology Group. $J Clin Oncol. 2005;23(15):3562-3567.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Cisplatin : CDDP');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (9, 2, 'Cisplatin/Fluorouracil/Cetuximab', '<b>Cisplatin/Fluorouracil/Cetuximab</b>', '<b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximuum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1. <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week', 'High', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cetuximab : Erbitux<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (10, 2, 'Cisplatin + radiation', '<b>Cisplatin + radiation</b>', '<b>Cisplatin</b> 75 mg/m<sup>2</sup>  IV, days 1,22, 43 concomitantly with radiation', 'High', 'Franchin G, Minatel E, Politi D, et al. Postoperative reduced dose of cisplatin concomitant with radiatoin therapy in high-risk head and neck squamous cell carcinoma. $Cancer. 2009;115(11):2464-2471.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Cisplatin : CDDP');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (11, 2, 'CP', '<b>CP</b>', '<b>Cisplatin</b> 75 mg/m<sup>2</sup>, day 1 and<br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> over 3 hours on day 1<br> Repeat cycle every 21 days', 'High', 'Gibson MK, Li Y, Murphy B, et al. Randomized phase III evaluation of cisplatin plus fluorouracil versus cisplatin plus paclitaxel in advanced head and neck cancer (E1395): an intergroup trial of the Eastern Cooperative Oncology Group. $J Clin Oncol. 2005;23(15):3562-3567.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Cisplatin : CDDP<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (12, 2, 'TPF', '<b>TPF</b>', '<b>Docetaxel<sup>b</sup></b> 75mg/m<sup>2</sup> IV over 1 hour, day1<br> <b>Cisplatin</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Fluorouraacil</b> 750 mg/m<sup>2</sup>/d, days 1-5<br> Repeat every 21 days<br> <b>OR</b><br> <b>Docetaxel<sup>b</sup></b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for 3 cycles', 'High', 'Vermorken JB, Remenar E, Van Herpen C, et al. Cisplatin, fluorouracil, and docetaxel in unresectable head and neck cancer. $N Engl J Med. 2007;357(17):1695-1704.$<br> Posner MR, Hershock DM, Blajman CR, et al. Cisplatin and fluorouracil alone or with docetaxel in head and neck cancer. $N Engl J Med. 2007;357(17):1705-1715.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Docetaxel : Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (13, 2, 'Cetuximab', '<b>Cetuximab</b>', '<b>Cetuximab</b> 400 mg/m<sup>2</sup> loading dose over 120 minutes, <b>THEN</b> 250 mg/m<sup>2</sup> over 60 minutes, every 7 days', 'Low', 'Bonner JA, Harari PM, Giralt J, et al. Radiotherapy plus cetuximab for locoregionally advanced head and neck cancer: 5-year survival datat from a phase 3 randomised trial, and relation between cetuximab-induced rash and surival. $Lancet Oncol.2010;11(1):21-28.$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Cetuximab : Erbitux');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (14, 5, '5-Fluorouracil + Cisplatin', '<b>5-Fluorouracil + Cisplatin</b>', '<b>5-Fluorouracil</b>1,000 mg/m<sup>2</sup>/day IV continuous infusion on days 1-5<br><b>Cisplatin</b>100 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle on weeks 1, 5, 8, and 11.', 'High', '$Kies MS, et al. Cancer 1987;60:2156-2160.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (15, 5, 'Irinotecan + Cisplatin', '<b>Irinotecan + Cisplatin</b>', '<b>Irinotecan</b>65 mg/m<sup>2</sup> IV weekly for 4 weeks<br><b>Cisplatin</b>30 mg/m<sup>2</sup> IV weekly for 4 weeks<br>Repeat cycle every 6 weeks.', 'High', '$Ilson DH, et al. J Clin Oncol 1999;17:3270-3275.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (16, 5, 'Paclitaxel + Cisplatin', '<b>Paclitaxel + Cisplatin</b>', '<b>Paclitaxel</b>200 mg/m<sup>2</sup> IV over 24 hours on day 1<br><b>Cisplatin</b>75 mg/m<sup>2</sup> IV on day 2<br>Repeat cycle every 21 days. G-CSF support is recommended.', 'High', '$Ilson DH, et al. J Clin Oncol 1998;16:1826-1834.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Cisplatin : CDDP<br>Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (17, 5, 'Capecitabine + Oxaliplatin', '<b>Capecitabine + Oxaliplatin</b>', '<b>Capecitabine</b>1,000 mg/m<sup>2</sup> PO bid on days 1-14<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle every 21 days.', 'Moderate', '$Van Meerten E, et al. Br J Cancer 2007;96:1348-1352.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (18, 5, 'ECF', '<b>ECF</b>', '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Cisplatin</b>60 mg/m<sup>2</sup> IV on day 1<br><b>5-Fluorouracil</b>200 mg/m<sup>2</sup>/day IV continuous infusion for 24 weeks.<br>Repeat cycle every 21 days.', 'High', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Cisplatin : CDDP<br>Epirubicin : Ellence, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (19, 5, 'EOF', '<b>EOF</b>', '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br><b>5-Fluorouracil</b>200 mg/m<sup>2</sup>/day IV continuous infusion for 24 weeks<br>Repeat cycle every 21 days.', 'Moderate', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', '<b>Hepatic Impairment</b><ul><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Epirubicin : Ellence, various<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (20, 5, 'ECX', '<b>ECX</b>', '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Cisplatin</b>60 mg/m<sup>2</sup> IV on day 1<br><b>Capecitabine</b>625 mg/m<sup>2</sup> PO bid continuously<br>Repeat cycle every 21 days.', 'High', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Capecitabine : Xeloda<br>Cisplatin : CDDP<br>Epirubicin : Ellence, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (21, 5, 'EOX', '<b>EOX</b>', '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br><b>Capecitabine</b>625 mg/m<sup>2</sup> PO bid times 21 days<br>Repeat cycle every 21 days.', 'Moderate', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Capecitabine : Xeloda<br>Epirubicin : Ellence, various<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (22, 5, 'Paclitaxel', '<b>Paclitaxel</b>', '<b>Paclitaxel</b>250 mg/m<sup>2</sup> IV over 24 hours on day 1<br>Repeat cycle every 21 days. G-CSF support is recommended.', 'Moderate', '$Ajani JA, et al. Semin Oncol 1995;22 (Suppl 6):35-40.$', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Paclitaxel : Taxol');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (23, 6, 'DCF', '<b>DCF</b>', '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1-3 hours, day 1<br> <b>Fluorouracil</b> 750 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 21 days', 'High', 'Van Custem E, Moiseyenko VM, Tjulandin S, et al. Phase III study of docetaxel and cisplatin plus florouracil compared with cisplatin and flouorouracil as first-line therapy for advanced gastric cancer: a report of the V325 Study Group. $J Clin Oncol. 2006;24(31):4991-4997$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Docetaxel : Taxotere<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (24, 6, 'Docetaxel/Cisplatin', '<b>Docetaxel/Cisplatin</b>', '<b>Docetaxel</b> 85 mg/m<sup>2</sup> IV over 1 hour, day 1,<br> <b>FOLLOWED BY</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1<br> Repeat every 21 days for a maximum of 8 cycles', 'High', 'Roth AD, Maibach R, Martinelli G, et al. Docetaxel (Taxotere)-cisplatin (TC): an effective drug combination in gastric carcinoma. $Ann Oncol. 2000;11(3):301-306$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Cisplatin : CDDP<br>Docetaxel : Taxotere');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (25, 6, 'ECF', '<b>ECF</b>', '<b>Epirubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 60 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 200 mg/m<sup>2</sup>/d CIVI, for up to 6 months<br> Repeat every 21 days for a maximum of 8 cycles', 'High', 'Waters JS, Norman A, Cunningham D, et al. Long-term survival afer epirubicin, cisplatin and fluorouracil for gastric cancer: results of a randomized trial. $Br J Cancer. 1999;80(1-2):269-272.$', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'Cisplatin : CDDP<br>Epirubicin : Ellence, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (26, 6, 'ELF', '<b>ELF</b>', '<b>Leucovorin calcium</b> 300 mg/m<sup>2</sup>/d IV over 10 minutes, <b>FOLLOWED BY</b><br> <b>Etoposide</b> 120 mg/m<sup>2</sup>/d IV over 50 minutes,<br> <b>THEN</b><br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV  bolus<br> Give all agents on days 1-3; repeat every 22 days', 'Low', 'Vanhoefer U, Rougier P, Wilke H, et al. Final results of a randomized phase III trial of sequential high-dose methotrexate, fluorouracil, and doxorubicin versus etoposide, leucovorin, and fluorouracil versus infusional flurorouracil and cisplatin in advanced gastric cancer: a trial of the European Organization for Research and Treatment of Cancer Gasttrointestinal Tract Cancer Cooperative Group. $J Clin Oncol. 2000;18(41):2648-2657$', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Etoposide : VP-16<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (27, 6, '5-FU/LV and Radiation', '<b>5-FU/LV and Radiation</b>', '<b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days,<br> <b>FOLLOWED BY</b><br> <b>RT</b> 4,500 cGy (180cGy/d), 5 days a week for 5 weeks<br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup>/d IV, on the first 4 and last 3 days of RT<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d, on the first 4 and last 3 days of RT,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days<br>repeat 28 days X 2 cycles', 'Low', 'Macdonald JS, Smalley SR, Benedetti J, et al. Chemoradiotherapy afer surgery compared with surgery alone for adenocarcinoma of the stomach or gastroesophageal junction. $N Engl J Med. 2001;345(10):725-730.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (28, 6, 'Trastuzumab + Cisplatin + Capecitabine or Fluorouracil', '<b>Trastuzumab + Cisplatin + Capecitabine or Fluorouracil</b>', '<b>Trastuzumab</b> 8 mg/kg IV, day 1 of the first cycle <b>THEN</b>  6 mg/kg IV, day 1<br><br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1. <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14 <br> Repeat  every 21 days<br> <b>OR</b><br> <b>Fluorouracil</b> 800 mg/m<sup>2</sup>/d CIVI for 5 days of each 3-week cycle<br> Repeat for 6 cycles', 'High day 1, low for the remaining treatment days', 'Bang YJ, Van Cutsem E, Feyereislava A, et al. Trastuzumab in combination with chemotherapy versus chemotherapy alone for treatment of HER2-positive advanced gastric or gastro-oesophageal junction cancer (ToGA): a phase 3 open-label, randomised controlled trial. $Lancet. 2010;376(99742):687-697.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Capecitabine : Xeloda<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU<br>Trastuzumab : Herceptin');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (29, 6, 'Capecitabine', '<b>Capecitabine</b>', '<b>Capecitabine</b> 1,250 mg/m<sup>2</sup> days 1-14,  <br> Repeat every 21 days for  6 cycles', 'Low', 'Hong YS, Song SY, Lee SI, et al. A phase II trial of capecitabine in previously untreated patients with advanced and/or metastatic gastric cancer. $Ann Oncol. 2004;15(9):1344-1347.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li></ul>', 'Capecitabine : Xeloda');
