INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (32, 10, 'FOLFOX4', '<b>FOLFOX4', '<b>Oxaliplatin</b>85 mg/m<sup>2</sup> IV on day 1<br><b>5-Fluorouracil</b>400 mg/m<sup>2</sup> IV bolus, followed by 600 mg/m<sup>2</sup> IV continuous infusio for 22 hours on days 1 and 2<br><b>Leucovorin</b>200 mg/m<sup>2</sup> IV on days 1 and 2 as a 2-hour infusion before 5-fluorouracil<br>Repeat cycle every 2 weeks.', 'Moderate', '$Qin S, et al. J Clin Oncol 2010;28:303S (abstract 408).$', '<b>Hepatic Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Oxaliplatin : Eloxatin, Oxaliplatin, various');
