INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (48, 11, 'FOLFIRINOX', '<b>FOLFIRINOX</b>', '<b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV over 2 hours <b>FOLLOWED BY Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours with the addition, after 30 minutes <b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes; <b>IMMEDIATELY FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus, <b>THEN</b><br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> IV over 46 hours<br> Repeat cycle every 2 weeks', 'Moderate', 'Conroy T, Desseigne F, Ychou M, et al. FOLFIRINOX versus gemcitabine for metastatic pancreatic cancer. $N Engl J Med. 2011;364(19):1817-1825.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Irinotecan:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (49, 11, 'Gemcitabine/Capecitabine', '<b>Gemcitabine/Capecitabine</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes, days 1, 8<br> <b>Capecitabine</b> 650 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days.', 'Low', 'Herrmann R, Bodoky G, Ruhstaller T, et al. Gemcitabine plus capecitabine compared with gemcitabine alone in advanced pancreatic cancer: a randomized, multicenter, phase III trial of the Swiss Group for Clinical Cancer Research and the Central European Cooperative Oncology Group. $J Clin Oncol. 2007;25(16):221-2217.$', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Capecitabine : Xeloda<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (50, 11, 'Gemcitabine/Erlotinib', '<b>Gemcitabine/Erlotinib</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes once weekly for 7 weeks,<br> <b>FOLLOWED BY</b> 1 week of rest<br> <b>Erlotinib</b> 100 <b>OR</b> 150 mg/d PO<br> Repeat subsequent cycles once weekly for 3 consecutive weeks out of every 4 weeks', 'Low', 'Moore MJ, Goldstein D, Hamm J, et al. Erlotinib plus gemcitabine compared with gemcitabine alone in patients with advanced pancreatic cancer: a phase III trial of the National Cancer institute of Canada Clinical Trials Group. $J Clin Oncol. 2007;25(15):1960-1966.$', '<b>Hepatic Impairment</b><ul><li>Erlotinib:<br>If AST > 3 upper normal limit, 50 mg  <br>                                                        If direct bilirubin 1-7 mg/dL, 75 mg </li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Erlotinib:<br>No dose reduction </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Erlotinib : Tarceva<br>Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (51, 11, 'GEMOX', '<b>GEMOX</b>', '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>, day 1 as 100-minute infusion<br> <b>Oxaliplatin</b> 100 mg/m<sup>2</sup>, day 2 as 2-hour infusion<br> Repeat every 14 days', 'Moderate', 'Louvet C, Labianca R, Hammel P, at al. Gemcitabine in combination with oxaliplatin compared with gemcitabine alone in locally advanced or metastatic pancreatic cancer: results of a GERCOR and GISCAD phase III trial. $J Clin Oncol. 2005;23(15):3509-3516.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Gemcitabine : Gemzar, various<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (52, 11, 'Gemcitabine', '<b>Gemcitabine</b>', '<b>Gemcitabine 1,000 mg/m<sup>2</sup> IV over 30 minutes once weekly for 7 weeks,<br> <b>FOLLOWED BY</b> 1 week of rest<br> Repeat subsequent cycles once weekly for 3 consecutive weeks out of every 4 weeks', 'Low', 'Burris HA 3rd, Moore MJ, Andersen J, et al. Improvements in survivial and clinical benefit with gemcitabine as first-line therapy for patients with advanced pancreas cancer: a randomized trial. $J Clin Oncol. 1997;15(6):2403-2413.$', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Gemcitabine : Gemzar, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (53, 4, 'Cetuximab/Irinotecan', '<b>Cetuximab/Irinotecan</b>', '<b>Cetuximab</b> 400 mg/m<sup>2</sup> IV initial dose,<b>THEN</b> 250 mg/m<sup>2</sup> IV every 7 days<br> <b>Irinotecan</b> 350 mg/m<sup>2</sup> IV every 21 days<br> <b>OR</b> 180 mg/m<sup>2</sup> IV every 14 days', 'Moderate', 'Cunningham D, Humblet Z, Siena S,et al. Cetuximab monothherapy and cetuximab plus irinotecan in irinotecan-refractory metastatic colorectal cancer. $N Engl J Med. 2004;351(4):337-345.$', '<b>Hepatic Impairment</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Irinotecan:<br>No dose reduction </li></ul>', 'Cetuximab : Erbitux');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (54, 4, '5-FU/LV (Mayo regimen)', '<b>5-FU/LV (Mayo regimen)</b>', '<b>Fluorouracil</b> 370mg/m<sup>2</sup>/d IV bolus 1 hour after start of leucovorin, days 1-5<br> <b>FOLLOWING Leucovorin calcium</b> 200 mg/m<sup>2</sup>/d IV bolus, days 1-5<br> Repeat at 4 and 8 weeks, and every 5 weeks thereafter', 'Low ', 'Poon MA, O''Connell MJ, Moertel CG. Biochemical modulation of fluorouracil: evidence of significant improvement of surival and quality of life in patients with advanced colorectal carcinoma. $J Clin Oncol. 1989;7(10):1407-1418$', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', '');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (55, 4, '5-FU/LV (Roswell Park regimen)', '<b>5-FU/LV (Roswell Park regimen)</b>', '<b>Fluorouracil</b> 600 mg/m<sup>2</sup>/d IV bolus 1 hour after start of leucovorin, days 1,8, 15, 22, 29, 35<br> <b>Leucovorin calcium</b> 500 mg/m<sup>2</sup>/d IV over 2 hours, days 1, 8, 15, 22, 29, 35<br> Repeat every 8 weeks', 'Low', 'Petreli N, Douglass HO Jr, Herrera L, et al. The modulation of fluroouracil with leucovorin in metastatic colorectal carcinoma: a prospective randomized phase III trial. $J Clin Oncol. 1989;7(10):1419-1426$<br> Petrelli N, Herrera L, Rustum Y, et al. A prospective randomized trial of 5-fluorouracil versus 5-fluorouracil and high-dose leucovorin versus 5-fluorouracil and methotrexate in previoulsy untreated patients with advanced colorectal carcinoma. $J Clin Oncol. 1987;5(10):1559-1565.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (56, 4, '5-FU/LV + Bevacizumab', '<b>5-FU/LV + Bevacizumab</b>', '<b>Fluorouracil</b> 600 mg/m<sup>2</sup>/d IV bolus 1 hour after start of leucovorin, days 1,8, 15, 22, 29, 35<br> <b>Leucovorin calcium</b> 500 mg/m<sup>2</sup>/d IV over 2 hours, days 1, 8, 15, 22, 29, 35<br> Repeat every 8 weeks<br> <b>Bevacizumab</b> 5mg/kg IV<br> Repeat every 14 days', 'Low', 'Kabbinavar FF, Hambleton J, Mass RD, Hurwitz HI, Bergland E, Sarkar S. Combined analysis of efficacy: the addition of bevacizumb to flurouracil/leucovorin improves survival for patients with metastatic colorectal cancer. $J Clin Oncol. 2005;23(16):3706-3712.$<br> Hurwitz HI, Fehrenbacher L, Hainsworth JD, et al. Bevacizumab in combination with fluorouracil and leucovorin; an active regimen for firstline metastatic colorectal cancer. $J Clin Oncol. 2005;23(15):3502-3508$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Bevacizumab : Avastin<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (57, 4, 'FLOX', '<b>FLOX</b>', '<b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV over 2 hours, days 1, 15, 29<br> <b>Leucovorin calcium</b> 500 mg/m<sup>2</sup> IV over 2 hours, days 1, 8, 15, 22, 29, 36<br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup> IV bolus 1 hour after the infusion begins, days 1, 8, 15, 22,    29, 36<br> Repeat every 8 weeks for 3 cycles', 'Moderate', 'Kuebler JP, Wieand HS, O''Connell MJ, et al. Oxaliplatin combined with weekly bolus fluorouracil and leucovorin as surgical adjuvant chemotherapy for stage II and III colon cancer: results from NSABP C-07. $J Clin Oncol. 2007;25(16):2198-2204.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (58, 4, 'FOLFIRI', '<b>FOLFIRI</b>', '<b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes, day 1<br> <b>Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours during irinotecan infusion,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus,<br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> CIVI over 46 hours<br> Repeat every 14 days', 'Moderate', 'Tournigand C, Andre T, Achille E, et al. FOLFIRI  followed by FOLFOX6 or the reverse sequence in advanced colorectal cancer: a randomized GERCOR study. $J Clin Oncol. 2004;22(2):229-237$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (59, 4, 'FOLFIRI + Bevacizumab', '<b>FOLFIRI + Bevacizumab</b>', '<b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes, day 1<br> <b>Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours, day 1<br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus, day 1<br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> CIVI over 46 hours, day 1<br> <b>Bevacizumab</b> 5 mg/kg IV, day 1<br> Repeat every 14 days', 'Moderate', 'Fuchs CS, Marshall J, Mitchell E, et al. Randomized, controlled trial of irinotecan plus infusional, bolus, or oral fluoropyrimidines in first-line treatment of metastatic colorectal cancer: results from the BICC-C Study. $J Clin Oncol. 2007;25(30):4779-4786.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Bevacizumab : Avastin<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (60, 4, 'FOLFIRI + Cetuximab', '<b>FOLFIRI + Cetuximab</b>', '<b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes, day 1<br> <b>Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours during irinotecan infusion,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus,<br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> CIVI over 46 hours<br> Repeat every 14 days<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV initial dose, <b>THERE AFTER</b> 250mg/m<sup>2</sup> IV weekly', 'Moderate', 'Van Cutsem E, Nowacki M, Lang I, et al. Randomized phase III study of irinotecan and 5-FU/FA with or without cetuximab in the first-line treatment of patients with metastatic colorectal cancer (mCRC): the CRYSTAL trial. $J Clin Oncol. 2007;25:4000 (abstract).$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Cetuximab : Erbitux<br>Fluorouracil : 5-FU');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (61, 4, 'FOLFOX4', '<b>FOLFOX4</b>', '<b>Day 1</b><br><b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV AND <br> <b>Leucovorin calcium</b> 200 mg/m<sup>2</sup> over 120 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus over 2-4 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 600mg/m<sup>2</sup> CIVI over 22 hours,<br><b>Day 2</b><br><b>Leucovorin calcium</b> 200 mg/m<sup>2</sup> IV over 120 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400mg/m<sup>2</sup> IV bolus over 2-4 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 600mg/m<sup>2</sup> CIVI over 22 hours,<br> Repeat  every 14 days', 'Moderate', 'Richard M. Goldberg, et al.Pooled Analysis of Safety and Efficacy of Oxaliplatin Plus Fluorouracil/Leucovorin Administered Bimonthly in Elderly Patients With Colorectal Cancer.$http://jco.ascopubs.org/content/24/25/4085.abstract$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (62, 4, 'FOLFOX4 + Bevacizumab', '<b>FOLFOX4 + Bevacizumab</b>', '<b>Day 1</b><br><b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV <b>AND</b><br> <b>Leucovorin calcium</b> 200 mg/m<sup>2</sup> over 120 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus over 2-4 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 600mg/m<sup>2</sup> CIVI over 22 hours, <br><b>Bevacizumab</b> 5 mg/kg IV.<br><b>Day 2</b><br> <b>Leucovorin calcium</b> 200 mg/m<sup>2</sup> IV over 120 minutes, day 2, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 400mg/m<sup>2</sup> IV bolus over 2-4 minutes, <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 600mg/m<sup>2</sup> CIVI over 22 hours,<br> Repeat  every 14 days <br> Repeat every 14 days', 'Moderate', 'Saltz LB, Clarke S, Diaz-Rubio E, et al. Bevacizumab in combination with oxaliplatin-based chemotherapy as first-line therapy in metastatic colorectal cancer: a randomized phase III study. $J Clin Oncol. 2008;26(18)2013-2019.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Bevacizumab : Avastin<br>Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
INSERT INTO RegimenDetail (pk, SubCancerTypeId2, disp_name, name, schedule, emetogenic_potential, reference, dosage_modifications, brand_names) VALUES (63, 4, 'FOLFOX4 + panitumumab ', '<b>FOLFOX4 + panitumumab</b> ', '<b>Panitumumab</b> 6 mg/kg IV over 60 minutes <br> <b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV, day 1<br> <b>Leucovorin calcium</b> 200 mg/m<sup>2</sup> IV on day 1<br> <b>FOLLOWED BY Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus, <b>FOLLOWED BY Fluorouracil</b> 600mg/m<sup>2</sup> IV infusion over 22 hours on days 1 and 2<br> Repeat cycle every 14 days', 'Moderate', 'Douillard JY, Siena S, Cassidy J, et al. Randomized, phase III trial of panitumumab with infusional fluorouracil, leucovorin, and oxaliplatin (FOLFOX4) versus FOLFOX4 alone as first-line treatment in patients with previously untreated metastatic colorectal cancer: the PRIME study. $J Clin Oncol. 2010;28(31):4697-4705.$', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various');
